insert  into `t_company_details`(`Id`,`Name`,`Contact_Number`,`Email_Address`,`Location_Address`,`Fackbook_Link`,`Twitter_Link`,`Youtube_Link`,`Googleplus_Link`,`Logo`,`Company_Description`,`status`) values (1,'DCSI TECHNOLOGY','77645388','info@dcsitechnology.bt','Department of Cottage and Small Industries, MoEA','https://www.facebook.com/dcsitechnology','https://www.twitter.com/dcsitechnology','https://www.Youtube.com/dcsitechnology','https://www.google.com/dcsitechnology','dcsilogoblack.png','<p style=\"text-align: justify; box-sizing: inherit; margin-bottom: 15px;\"><font face=\"Arial\"><span style=\"font-size: 14px;\">Guided by the philosophy of Gross National Happiness, the Department of Cottage&nbsp;</span></font><span style=\"font-size: 14px; font-family: Arial;\">&amp; Small Industry under the Ministry of Economic Affairs is the premier agency&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">promoting the growth of vibrant and sustainable CSIs contributing to the overall&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">socio-economic development in the country.</span></p><p style=\"text-align: justify; box-sizing: inherit; margin-bottom: 15px;\"><font face=\"Arial\"><span style=\"font-size: 14px;\">The Department was created in 2010 to give focused attention to CSI development. It&nbsp;</span></font><span style=\"font-size: 14px; font-family: Arial;\">is mandated to create an enabling environment to facilitate and support the sustainable&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">growth and development of CSIs for equitable income distribution, employment&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">generation, and balanced regional development.</span></p><p style=\"text-align: justify; box-sizing: inherit; margin-bottom: 15px;\"><font face=\"Arial\"><span style=\"font-size: 14px;\">The Development has gained momentum since its establishment. However, the sector&nbsp;</span></font><span style=\"font-size: 14px; font-family: Arial;\">has been challenged by a myriad of factors such as the poor culture of entrepreneurship&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">and limited human capacity, limited access to finance, infrastructure, inadequate&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">business development supports, poor innovation and technology, and access to the&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">market that impeded it from realizing its full potential. Recognizing these challenges,&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">the government has accorded high priority for its development.</span></p><p style=\"text-align: justify; box-sizing: inherit; margin-bottom: 15px;\"><font face=\"Arial\"><span style=\"font-size: 14px;\">The CSI sector is designated as one of the five economic jewels and occupies a central&nbsp;</span></font><span style=\"font-size: 14px; font-family: Arial;\">position in the 12 Five Year Plan. The Start-up and CSI Development is one of&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">the flagship programs of the government with focused investment. Thus, various&nbsp;</span><span style=\"font-size: 14px; font-family: Arial;\">interventions are designed to promote the growth of the sector.</span></p>',NULL);

